import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { FaGithub, FaExternalLinkAlt, FaImages, FaTimes, FaChevronLeft, FaChevronRight } from 'react-icons/fa';

const projects = [
  {
    title: 'Hospital Database System',
    description: 'A comprehensive database management system for hospitals, featuring patient records, staff management, and appointment scheduling. Built with C# and SQL, this project demonstrates strong database design and management skills.',
    technologies: ['C#', 'SQL', 'Entity Framework', 'Windows Forms'],
    githubLink: null,
    liveLink: null,
    image: '/images/3rd Nominal Form.png',
    screenshots: '/Hospital-DB-Project.pdf',
  },
  {
    title: 'Create Your Own Adventure Game',
    description: 'An interactive text-based adventure game where players make choices that affect the story outcome. This project showcases strong programming logic and user interface design skills.',
    technologies: ['C#', 'Windows Forms', 'Object-Oriented Programming'],
    githubLink: 'https://github.com/MLWester/Create-Your-Own-Adventure-Game',
    liveLink: null,
    image: '/images/CYOA Class Diagram.png',
    screenshots: [
      '/images/CYOA Class Diagram.png',
      '/images/CYOA Create Charcter Activity Diagram.png',
      '/images/CYOA Attack Diagram.png',
      '/images/CYOA End Game Diagram.png',
      '/images/CYOA Sequence Diagram.png',
      '/images/CYOA Start Diagram.png'
    ],
  },
  {
    title: 'FitPro Gym Management',
    description: 'A gym management system designed to handle member registrations, class scheduling, and facility management. This project demonstrates full-stack development capabilities and database integration.',
    technologies: ['C#', 'SQL', 'Entity Framework', 'Windows Forms'],
    githubLink: 'https://github.com/MLWester/FitPro-Gym-Membership-Manager',
    liveLink: '/FitPro-Demo-Vid.mp4',
    image: '/images/fitpro 5.png',
    screenshots: [
      '/images/fitpro 5.png',
      '/images/fitpro 4.png',
      '/images/fitpro 3.png',
      '/images/fitpro 2.png',
      '/images/Fitpro 1.png'
    ],
  },
  {
    title: 'Project 4',
    description: 'Coming soon...',
    technologies: [],
    githubLink: '#',
    liveLink: '#',
    image: '/images/placeholder.jpg',
    screenshots: '#',
  },
];

const Projects = () => {
  const [selectedProject, setSelectedProject] = useState(null);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const openModal = (project) => {
    setSelectedProject(project);
    setCurrentImageIndex(0);
  };

  const closeModal = () => {
    setSelectedProject(null);
  };

  const nextImage = () => {
    if (selectedProject && Array.isArray(selectedProject.screenshots)) {
      setCurrentImageIndex((prevIndex) => 
        (prevIndex + 1) % selectedProject.screenshots.length
      );
    }
  };

  const prevImage = () => {
    if (selectedProject && Array.isArray(selectedProject.screenshots)) {
      setCurrentImageIndex((prevIndex) => 
        (prevIndex - 1 + selectedProject.screenshots.length) % selectedProject.screenshots.length
      );
    }
  };

  return (
    <div className="py-12">
      <motion.h1
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="text-4xl font-bold text-center mb-12"
      >
        My <span className="text-accent">Projects</span>
      </motion.h1>

      {/* Modal for screenshots */}
      {selectedProject && Array.isArray(selectedProject.screenshots) && (
        <div className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50">
          <div className="bg-secondary rounded-lg p-4 max-w-4xl w-full mx-4">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-xl font-bold">{selectedProject.title} Screenshots</h3>
              <button
                onClick={closeModal}
                className="text-white hover:text-accent transition-colors"
              >
                <FaTimes size={24} />
              </button>
            </div>
            <div className="relative">
              <img
                src={selectedProject.screenshots[currentImageIndex]}
                alt={`Screenshot ${currentImageIndex + 1}`}
                className="w-full h-auto rounded-lg"
              />
              <button
                onClick={prevImage}
                className="absolute left-4 top-1/2 transform -translate-y-1/2 bg-black bg-opacity-50 text-white p-2 rounded-full hover:bg-opacity-75 transition-colors"
              >
                <FaChevronLeft size={24} />
              </button>
              <button
                onClick={nextImage}
                className="absolute right-4 top-1/2 transform -translate-y-1/2 bg-black bg-opacity-50 text-white p-2 rounded-full hover:bg-opacity-75 transition-colors"
              >
                <FaChevronRight size={24} />
              </button>
            </div>
            <div className="mt-4 text-center text-white">
              Image {currentImageIndex + 1} of {selectedProject.screenshots.length}
            </div>
          </div>
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 gap-8 max-w-6xl mx-auto px-4">
        {projects.map((project, index) => (
          <motion.div
            key={project.title}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1 }}
            className="bg-secondary rounded-lg overflow-hidden shadow-lg"
          >
            <div className="h-48 bg-gray-700">
              {project.image.endsWith('.pdf') ? (
                <div className="w-full h-full flex items-center justify-center">
                  <span className="text-white text-xl">PDF Document</span>
                </div>
              ) : (
                <img
                  src={project.image}
                  alt={project.title}
                  className="w-full h-full object-cover"
                />
              )}
            </div>
            <div className="p-6">
              <h2 className="text-xl font-bold mb-2">{project.title}</h2>
              <p className="text-gray-300 mb-4">{project.description}</p>
              <div className="flex flex-wrap gap-2 mb-4">
                {project.technologies.map((tech) => (
                  <span
                    key={tech}
                    className="bg-accent text-white text-sm px-3 py-1 rounded-full"
                  >
                    {tech}
                  </span>
                ))}
              </div>
              <div className="flex space-x-4">
                {project.githubLink && (
                  <a
                    href={project.githubLink}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="bg-gray-700 hover:bg-gray-600 text-white font-bold py-2 px-4 rounded-lg flex items-center space-x-2 transition duration-300"
                  >
                    <FaGithub />
                    <span>Source Code</span>
                  </a>
                )}
                {project.liveLink && (
                  <a
                    href={project.liveLink}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="bg-accent hover:bg-blue-600 text-white font-bold py-2 px-4 rounded-lg flex items-center space-x-2 transition duration-300"
                  >
                    <FaExternalLinkAlt />
                    <span>Live Demo</span>
                  </a>
                )}
                {Array.isArray(project.screenshots) ? (
                  <button
                    onClick={() => openModal(project)}
                    className="bg-gray-700 hover:bg-gray-600 text-white font-bold py-2 px-4 rounded-lg flex items-center space-x-2 transition duration-300"
                  >
                    <FaImages />
                    <span>View Screenshots ({project.screenshots.length})</span>
                  </button>
                ) : (
                  <>
                    <a
                      href={project.screenshots}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="bg-gray-700 hover:bg-gray-600 text-white font-bold py-2 px-4 rounded-lg flex items-center space-x-2 transition duration-300"
                    >
                      <FaImages />
                      <span>View Documentation</span>
                    </a>
                    <a
                      href={project.screenshots}
                      download="Hospital-DB-Project.pdf"
                      className="bg-gray-700 hover:bg-gray-600 text-white font-bold py-2 px-4 rounded-lg flex items-center space-x-2 transition duration-300"
                    >
                      <FaImages />
                      <span>Download Documentation</span>
                    </a>
                  </>
                )}
              </div>
            </div>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default Projects;
