import React from 'react';
import { motion } from 'framer-motion';
import { FaCode, FaDatabase, FaTools, FaLanguage } from 'react-icons/fa';

const Skills = () => {
  const skills = [
    {
      category: 'Programming Languages',
      icon: <FaLanguage className="text-lime-400" size={24} />,
      items: ['C#', 'Python', 'JavaScript', 'SQL', 'HTML/CSS']
    },
    {
      category: 'Web Development',
      icon: <FaCode className="text-lime-400" size={24} />,
      items: ['React', 'Node.js', 'Express', 'Tailwind CSS', 'Bootstrap']
    },
    {
      category: 'Database & Tools',
      icon: <FaDatabase className="text-lime-400" size={24} />,
      items: ['SQL Server', 'MySQL', 'MongoDB', 'Entity Framework', 'Git']
    },
    {
      category: 'Other Skills',
      icon: <FaTools className="text-lime-400" size={24} />,
      items: ['Data Analysis', 'Project Management', 'Agile Methodologies', 'UI/UX Design', 'Problem Solving']
    }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-b from-black to-gray-900 py-16 px-4 sm:px-6 lg:px-8">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="max-w-6xl mx-auto"
      >
        <motion.h1
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
          className="text-5xl font-bold text-center mb-16 bg-clip-text text-transparent bg-gradient-to-r from-lime-400 to-lime-600"
        >
          My <span className="text-lime-400">Skills</span>
        </motion.h1>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {skills.map((skill, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.2 + index * 0.1 }}
              className="bg-gray-900 rounded-xl p-6 shadow-2xl border border-lime-500/20"
            >
              <div className="flex items-center space-x-4 mb-6">
                {skill.icon}
                <h2 className="text-2xl font-bold text-white">{skill.category}</h2>
              </div>
              
              <div className="flex flex-wrap gap-2">
                {skill.items.map((item, itemIndex) => (
                  <span
                    key={itemIndex}
                    className="px-4 py-2 bg-lime-500/10 text-lime-400 rounded-full text-sm"
                  >
                    {item}
                  </span>
                ))}
              </div>
            </motion.div>
          ))}
        </div>
      </motion.div>
    </div>
  );
};

export default Skills;
