import React from 'react';
import { motion } from 'framer-motion';
import { FaGithub, FaExternalLinkAlt } from 'react-icons/fa';

const projects = [
  {
    title: 'Hospital Database System',
    description: 'A comprehensive database management system for hospitals, featuring patient records, staff management, and appointment scheduling. Built with C# and SQL, this project demonstrates strong database design and management skills.',
    technologies: ['C#', 'SQL', 'Entity Framework', 'Windows Forms'],
    githubLink: '#', // Add your GitHub link here
    liveLink: '#', // Add your live demo link here if available
    image: '/Hospital-DB-Project.pdf',
  },
  {
    title: 'Create Your Own Adventure Game',
    description: 'An interactive text-based adventure game where players make choices that affect the story outcome. This project showcases strong programming logic and user interface design skills.',
    technologies: ['C#', 'Windows Forms', 'Object-Oriented Programming'],
    githubLink: '#', // Add your GitHub link here
    liveLink: '#', // Add your live demo link here if available
    image: '/images/CYOA/cyoa1.jpg', // You can use any of the CYOA images
  },
  {
    title: 'FitPro Gym Management',
    description: 'A gym management system designed to handle member registrations, class scheduling, and facility management. This project demonstrates full-stack development capabilities and database integration.',
    technologies: ['C#', 'SQL', 'Entity Framework', 'Windows Forms'],
    githubLink: '#', // Add your GitHub link here
    liveLink: '#', // Add your live demo link here if available
    image: '/images/fitpro/fitpro1.jpg', // You can use any of the fitpro images
  },
  {
    title: 'Project 4',
    description: 'Coming soon...',
    technologies: [],
    githubLink: '#',
    liveLink: '#',
    image: '/images/placeholder.jpg',
  },
];

const Projects = () => {
  return (
    <div className="py-12">
      <motion.h1
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="text-4xl font-bold text-center mb-12"
      >
        My <span className="text-accent">Projects</span>
      </motion.h1>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-8 max-w-6xl mx-auto px-4">
        {projects.map((project, index) => (
          <motion.div
            key={project.title}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1 }}
            className="bg-secondary rounded-lg overflow-hidden shadow-lg"
          >
            <div className="h-48 bg-gray-700">
              {project.image.endsWith('.pdf') ? (
                <div className="w-full h-full flex items-center justify-center">
                  <span className="text-white text-xl">PDF Document</span>
                </div>
              ) : (
                <img
                  src={project.image}
                  alt={project.title}
                  className="w-full h-full object-cover"
                />
              )}
            </div>
            <div className="p-6">
              <h2 className="text-xl font-bold mb-2">{project.title}</h2>
              <p className="text-gray-300 mb-4">{project.description}</p>
              <div className="flex flex-wrap gap-2 mb-4">
                {project.technologies.map((tech) => (
                  <span
                    key={tech}
                    className="bg-accent text-white text-sm px-3 py-1 rounded-full"
                  >
                    {tech}
                  </span>
                ))}
              </div>
              <div className="flex space-x-4">
                <a
                  href={project.githubLink}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="bg-gray-700 hover:bg-gray-600 text-white font-bold py-2 px-4 rounded-lg flex items-center space-x-2 transition duration-300"
                >
                  <FaGithub />
                  <span>Source Code</span>
                </a>
                {project.liveLink && (
                  <a
                    href={project.liveLink}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="bg-accent hover:bg-blue-600 text-white font-bold py-2 px-4 rounded-lg flex items-center space-x-2 transition duration-300"
                  >
                    <FaExternalLinkAlt />
                    <span>Live Demo</span>
                  </a>
                )}
              </div>
            </div>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default Projects;
