import React from 'react';
import { motion } from 'framer-motion';
import { FaDownload } from 'react-icons/fa';

const Resume = () => {
  const handleDownload = () => {
    // Replace with your actual resume PDF path
    const resumeUrl = '/resume.pdf';
    const link = document.createElement('a');
    link.href = resumeUrl;
    link.download = 'your-name-resume.pdf';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div className="py-12">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="max-w-4xl mx-auto px-4"
      >
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-4xl font-bold">
            My <span className="text-accent">Resume</span>
          </h1>
          <button
            onClick={handleDownload}
            className="bg-accent hover:bg-blue-600 text-white font-bold py-2 px-4 rounded-lg flex items-center space-x-2 transition duration-300"
          >
            <FaDownload />
            <span>Download PDF</span>
          </button>
        </div>

        <div className="bg-secondary rounded-lg shadow-lg p-4">
          <iframe
            src="/resume.pdf"
            className="w-full h-[800px]"
            title="Resume PDF"
          />
        </div>
      </motion.div>
    </div>
  );
};

export default Resume;
