import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { FaGithub, FaExternalLinkAlt, FaImages, FaTimes, FaChevronLeft, FaChevronRight, FaDownload, FaEye } from 'react-icons/fa';

const projects = [
  {
    title: 'Hospital Database System',
    description: 'A comprehensive database management system for hospitals, featuring patient records, staff management, and appointment scheduling. Built with C# and SQL, this project demonstrates strong database design and management skills.',
    technologies: ['C#', 'SQL', 'Entity Framework', 'Windows Forms'],
    githubLink: null,
    liveLink: null,
    image: '/images/3rd Nominal Form.png',
    screenshots: '/Hospital-DB-Project.pdf',
  },
  {
    title: 'Create Your Own Adventure Game',
    description: 'An interactive text-based adventure game where players make choices that affect the story outcome. This project showcases strong programming logic and user interface design skills.',
    technologies: ['C#', 'Windows Forms', 'Object-Oriented Programming'],
    githubLink: 'https://github.com/MLWester/Create-Your-Own-Adventure-Game',
    liveLink: null,
    image: '/images/CYOA Class Diagram.png',
    screenshots: [
      '/images/CYOA Class Diagram.png',
      '/images/CYOA Create Charcter Activity Diagram.png',
      '/images/CYOA Attack Diagram.png',
      '/images/CYOA End Game Diagram.png',
      '/images/CYOA Sequence Diagram.png',
      '/images/CYOA Start Diagram.png'
    ],
  },
  {
    title: 'FitPro Gym Management',
    description: 'A gym management system designed to handle member registrations, class scheduling, and facility management. This project demonstrates full-stack development capabilities and database integration.',
    technologies: ['C#', 'SQL', 'Entity Framework', 'Windows Forms'],
    githubLink: 'https://github.com/MLWester/FitPro-Gym-Membership-Manager',
    liveLink: '/FitPro-Demo-Vid.mp4',
    image: '/images/fitpro 5.png',
    screenshots: [
      '/images/fitpro 5.png',
      '/images/fitpro 4.png',
      '/images/fitpro 3.png',
      '/images/fitpro 2.png',
      '/images/Fitpro 1.png'
    ],
  },
  {
    title: 'Project 4',
    description: 'Coming soon...',
    technologies: [],
    githubLink: '#',
    liveLink: '#',
    image: '/images/placeholder.jpg',
    screenshots: '#',
  },
];

const Projects = () => {
  const [selectedProject, setSelectedProject] = useState(null);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const openModal = (project) => {
    setSelectedProject(project);
    setCurrentImageIndex(0);
  };

  const closeModal = () => {
    setSelectedProject(null);
  };

  const nextImage = () => {
    if (selectedProject && Array.isArray(selectedProject.screenshots)) {
      setCurrentImageIndex((prevIndex) => 
        (prevIndex + 1) % selectedProject.screenshots.length
      );
    }
  };

  const prevImage = () => {
    if (selectedProject && Array.isArray(selectedProject.screenshots)) {
      setCurrentImageIndex((prevIndex) => 
        (prevIndex - 1 + selectedProject.screenshots.length) % selectedProject.screenshots.length
      );
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-black to-gray-900 py-16 px-4 sm:px-6 lg:px-8">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="max-w-6xl mx-auto"
      >
        <motion.h1
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
          className="text-5xl font-bold text-center mb-16 bg-clip-text text-transparent bg-gradient-to-r from-lime-400 to-lime-600"
        >
          My <span className="text-lime-400">Projects</span>
        </motion.h1>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {projects.map((project, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.2 + index * 0.1 }}
              className="bg-gray-900 rounded-xl overflow-hidden shadow-2xl border border-lime-500/20"
            >
              <div className="p-6">
                <h2 className="text-2xl font-bold text-white mb-4">{project.title}</h2>
                <p className="text-gray-400 mb-6">{project.description}</p>
                
                <div className="flex flex-wrap gap-2 mb-6">
                  {project.technologies.map((tech, techIndex) => (
                    <span
                      key={techIndex}
                      className="px-3 py-1 bg-lime-500/10 text-lime-400 rounded-full text-sm"
                    >
                      {tech}
                    </span>
                  ))}
                </div>

                <div className="flex space-x-4">
                  <motion.a
                    href={project.githubLink}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-gray-300 hover:text-lime-400 transition-colors"
                    whileHover={{ scale: 1.1 }}
                  >
                    <FaGithub size={24} />
                  </motion.a>
                  <motion.a
                    href={project.liveLink}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-gray-300 hover:text-lime-400 transition-colors"
                    whileHover={{ scale: 1.1 }}
                  >
                    <FaExternalLinkAlt size={24} />
                  </motion.a>
                </div>
              </div>

              <div className="relative h-64 bg-gray-800">
                {project.screenshots ? (
                  <div className="absolute inset-0 flex items-center justify-center">
                    <span className="text-gray-400">PDF Document</span>
                  </div>
                ) : (
                  <img
                    src={project.image}
                    alt={project.title}
                    className="w-full h-full object-cover"
                  />
                )}
              </div>
            </motion.div>
          ))}
        </div>
      </motion.div>

      {/* Modal for screenshots */}
      {selectedProject && Array.isArray(selectedProject.screenshots) && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="fixed inset-0 bg-black/90 backdrop-blur-sm flex items-center justify-center z-50 p-4"
        >
          <motion.div
            initial={{ scale: 0.9, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            exit={{ scale: 0.9, opacity: 0 }}
            className="bg-gray-800 rounded-xl p-6 max-w-4xl w-full mx-4 shadow-2xl"
          >
            <div className="flex justify-between items-center mb-6">
              <h3 className="text-2xl font-bold text-white">{selectedProject.title} Screenshots</h3>
              <motion.button
                onClick={closeModal}
                className="text-white hover:text-lime-400 transition-colors p-2 rounded-full hover:bg-gray-700"
                whileHover={{ scale: 1.1 }}
              >
                <FaTimes size={24} />
              </motion.button>
            </div>
            
            <div className="relative">
              <motion.img
                key={currentImageIndex}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                src={selectedProject.screenshots[currentImageIndex]}
                alt={`Screenshot ${currentImageIndex + 1}`}
                className="w-full h-auto rounded-lg shadow-xl"
              />
              <motion.button
                onClick={prevImage}
                className="absolute left-4 top-1/2 transform -translate-y-1/2 bg-black/50 text-white p-3 rounded-full hover:bg-black/75 transition-colors"
                whileHover={{ scale: 1.1 }}
              >
                <FaChevronLeft size={24} />
              </motion.button>
              <motion.button
                onClick={nextImage}
                className="absolute right-4 top-1/2 transform -translate-y-1/2 bg-black/50 text-white p-3 rounded-full hover:bg-black/75 transition-colors"
                whileHover={{ scale: 1.1 }}
              >
                <FaChevronRight size={24} />
              </motion.button>
            </div>
            
            <div className="mt-6 text-center text-white text-lg">
              Image {currentImageIndex + 1} of {selectedProject.screenshots.length}
            </div>
          </motion.div>
        </motion.div>
      )}
    </div>
  );
};

export default Projects;
